import React, { useState } from "react";
import "./App.css";
import Post from "./Post";
import Comment from "./Comments";

function App() {
  const [posts, setPosts] = useState([]);

  const handleAddPost = (content) => {
    const newPost = {
      author: "John Doe", // You can replace with the logged-in user's name
      content,
      comments: [],
    };
    setPosts([...posts, newPost]);
  };

  const handleAddComment = (postId, commentText) => {
    const updatedPosts = [...posts];
    const postIndex = updatedPosts.findIndex((post) => post.content === postId);
    if (postIndex !== -1) {
      updatedPosts[postIndex].comments.push(commentText);
      setPosts(updatedPosts);
    }
  };

  return (
    <div className="App">
      <h1>LinkedIn Home Page</h1>
      <div className="add-post">
        <textarea
          placeholder="What's on your mind?"
          rows="4"
          cols="50"
          onChange={(e) => handleAddPost(e.target.value)}
        />
        <button>Post</button>
      </div>
      <div className="posts">
        {posts.map((post, index) => (
          <Post
            key={index}
            author={post.author}
            content={post.content}
            onComment={(comment) => handleAddComment(post.content, comment)}
          >
            {post.comments.map((comment, index) => (
              <Comment key={index} text={comment} />
            ))}
          </Post>
        ))}
      </div>
    </div>
  );
}

export default App;
